package com.rekj.core.util;

/**
 * 生成拼音(五笔unused)
 * 
 * @author jack
 * @date 2017年5月9日 上午12:15:06
 */
public class PinyinUtils {

	private static String strchinesefirstpy = "";

	/**
	 * 汉字转拼音
	 * e.g.    叙事曲 => XSQ
	 * 
	 * @param name
	 * @return 
	 * @author jack  2017年5月9日 上午12:24:10
	 */
	public static String toPinyin(String name) {
		StringBuilder stringBuilder = new StringBuilder();
		for (int i = 0; i < name.length(); i++) {
			char c = name.charAt(i);
			int num2 = (int) (c - '一');
			if (num2 >= 0 && num2 < strchinesefirstpy.length()) {
				stringBuilder.append(strchinesefirstpy.charAt(num2));
			} else {
				stringBuilder.append(c);
			}
		}
		return stringBuilder.toString();
	}

}

